mapper.select:
SELECT a.id, a.title, c.`name` typeName, d.`name` secondTypeName, DATE_FORMAT(a.create_time,'%Y-%m-%d') createTime, a.state FROM knowlg_content a LEFT JOIN knowlg_type c ON a.type_id = c.id LEFT JOIN knowlg_type d ON a.second_type_id = d.id WHERE a.create_id = #{userId}
AND a.title LIKE '%${title}%'
AND a.state = #{state}
AND a.type_id = #{typeId}
AND a.second_type_id = #{secondTypeId}
ORDER BY a.create_time DESC

mapper.update:
UPDATE knowlg_content
title = #{title}, `desc` = #{desc}, content = #{content}, type_id = #{typeId}, second_type_id = #{secondTypeId}
WHERE id = #{id}

UPDATE knowlg_content

	examine_id = #{examineId},
	examine_time = #{examineTime},
	state = #{examineState},
	examine_nopass_reason = #{examineNopassReason}

WHERE id = #{id}

mapper.select:
SELECT a.id, a.title, a.typeName, a.secondTypeName, a.createUser, a.createTime, a.examineUser, a.examineTime, a.state FROM (SELECT a.id, a.title, c.`name` typeName, d.`name` secondTypeName, a.type_id, a.second_type_id, e.user_name createUser, date_format(a.create_time,'%Y-%m-%d %k:%i') createTime, b.user_name examineUser, a.create_time, a.examine_time, date_format(a.examine_time,'%Y-%m-%d %k:%i') examineTime, a.state FROM sys_eve_user_staff e, sys_eve_user_staff b, knowlg_content a LEFT JOIN knowlg_type c ON a.type_id = c.id LEFT JOIN knowlg_type d ON a.second_type_id = d.id WHERE a.state != '1' AND b.user_id = a.examine_id AND e.user_id = a.create_id) a WHERE 1 = 1
AND a.title LIKE '%${title}%'
AND a.state = #{state}
AND a.type_id = #{typeId}
AND a.second_type_id = #{secondTypeId}
AND a.createUser like '%${createUser}%'
AND a.create_time >= #{startTime} AND #{endTime} >= a.create_time
AND a.examine_time >= #{examineStartTime} AND #{examineEndTime} >= a.examine_time
ORDER BY a.create_time DESC
